insert into `modx_site_content` values ('251', 'document', 'text/html', 'Анастасия', '', '', 'anastasiya-2017-06-05', '', '0', '0', '0', '49', '0', null, 'Наш долгожданный ребенок появился на свет благодаря замечательному акушеру-гинекологу Осиной Екатерине Александровне, человеку с золотыми руками. Все предыдущие исследования на причины бесплодия оказывались безрезультатными, бесконечные анализы и осмотры ничего не давали, но консультация в АльтраВите, последующая гистероскопия и основанное на ее результатах лечение, принесли свои результаты и спустя год лечения мы стали родителями! Хочу добавить, что весь персонал клиники отзывчив и внимателен к пожеланиям клиентов. Хочу выразить огромную признательность за их работу и подаренное нам счастье!', '1', '6', '18', '1', '1', '5', '1498831136', '0', '0', '0', '0', '0', '1498831136', '5', '', '0', '0', '0', '0', '0', 'Ticket', 'web', '1', 'otzyivyi-o-vrachax-ginekologax-endoskopistax.html/251-anastasiya-2017-06-05.html', '1', '0', '1', null);
